        font-size: 32px;
        line-height: 40px;
        margin: 0 0 32px;
        text-align: center;
        text-transform: none;
        @media screen and (max-width: 800px) {
            font-size: 24px;
            line-height: 29px;
        }
    }
    .help-wrapper{
        display: flex;
        gap: 20px;
        margin-bottom: 123px;
