components/common/navigation-list:
{{#if children}}
<a class="navPages-action main-item has-subMenu{{#if is_active}} activePage{{/if}}" href="{{url}}"
    data-collapsible="navPages-{{id}}">
    {{name}}
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list child-list container">
        {{#each children}}
        <li class="navPage-subMenu-item">
            {{#if children}}
            <a class="navPage-subMenu-action navPages-action has-subMenu{{#if is_active}} activePage{{/if}}"
                href="{{url}}" aria-label="{{name}}">
                {{name}}
                <svg data-src='{{cdn "assets/icons/arrow.svg"}}'></svg>
            </a>
            <ul class="navPage-childList" id="navPages-{{id}}">
                {{#each children}}
                <li class="navPage-childList-item">
                    <a class="navPage-childList-action navPages-action{{#if is_active}} activePage{{/if}}"
                        href="{{url}}" aria-label="{{name}}">
                        <span>{{name}}</span>
                        <svg data-src='{{cdn "assets/icons/arrow.svg"}}'></svg>
                    </a>
                </li>
                {{/each}}
            </ul>
            {{else}}
            <a class="navPage-subMenu-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}"
                aria-label="{{name}}">
                <span>{{name}}</span>
                <svg data-src='{{cdn "assets/icons/arrow.svg"}}'></svg>
            </a>
            {{/if}}
        </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="main-item navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}" aria-label="{{name}}">
    {{name}}
</a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="social-links">
        {{#each social_media}}
            <li>
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}
