pages/order-confirmation:
{{#partial "head"}}
{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}
<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.4/jquery.min.js"></script>
<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

{{{head.scripts}}}

{{/partial}}

{{#partial "page"}}
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

{{{ checkout.order_confirmation_content }}}

{{{ footer.scripts }}}

{{/partial}}

{{> layout/empty}}



<script>
var intervaldata = false;
var myInterval = setInterval(function () {
            if($('[data-test="order-confirmation-order-status-text"]').length > 0 && !$('[data-test="order-confirmation-order-status-text"]').hasClass('done')){
            $('[data-test="order-confirmation-order-status-text"]').find('span').html('If you have questions about your order, please contact <a href="mailto:econlinestoresupport@stryker.com">econlinestoresupport@stryker.com.</a>')
            $('[data-test="order-confirmation-order-status-text"]').addClass('done');
            intervaldata = true;
           
            if(intervaldata){
                clearInterval(myInterval);
            }
            }
        },150);
</script>
