                    {{#if name '===' "additional_products"}}
                        <a href=" {{{last (split value "|")}}}"> {{{first (split value "|")}}}</a>
                    {{/if}}
                {{/each}}
            </div>
        </div>
    </section>

    <section class="productView-details product-data">
        <div class="productView-product">
            {{#if product.brand}}
                <h2 class="productView-brand"{{#if schema}} itemprop="brand" itemscope itemtype="https://schema.org/Brand"{{/if}}>
                    <a href="{{product.brand.url}}"{{#if schema}} itemprop="url"{{/if}}><span{{#if schema}} itemprop="name"{{/if}}>{{product.brand.name}}</span></a>
                </h2>
            {{/if}}
            <h1 class="productView-title" {{#if schema}}itemprop="name"{{/if}}>{{product.title}}</h1>
            <div class="product-sku">
                {{#if customer }}
                
                {{#if product.can_purchase}}
                   {{#if product.pre_order}}
                     
                    {{else}}
                    <dt class="label-stock in-stock">{{lang 'products.current_stock'}}</dt>
                    {{/if}}
                {{else}}
                    <dt class="label-stock out-stock">{{lang 'products.out_stock'}}</dt>
                {{/if}}
                {{/if}}
                <dt class="productView-info-name sku-label"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku_name'}}</dt>
                <dd class="productView-info-value" data-product-sku{{#if schema}} itemprop="sku"{{/if}}>{{product.sku}}</dd>
            </div>
            
       
            
            {{#if product.call_for_price}}
                <p class="productView-price">
                    <span>{{product.call_for_price}}</span>
                </p>
            {{/if}}
            <div class="productView-price">
                {{#if customer }}
                    {{> components/products/price price=product.price schema_org=schema}}
                {{else}}
                    {{> components/common/login-for-pricing}}
                {{/if}}
            </div>
            <dt class="productView-info-name upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</dt>
            <dd class="productView-info-value" {{#unless product.upc}} style="display: none;"{{/unless}} data-product-upc>{{product.upc}}</dd>
            {{#if schema}}
                {{#if product.mpn}}<meta itemprop="mpn" content="{{product.mpn}}" />{{/if}}
                {{#if product.gtin}}<meta itemprop="gtin" content="{{product.gtin}}" />{{/if}}
            {{/if}}
            {{{region name="product_below_price"}}}
            <article class="productView-description"{{#if schema}} itemprop="description"{{/if}}>
                {{#if theme_settings.show_product_details_tabs}}
                    {{> components/products/description-tabs}}
                {{else}}
                    {{> components/products/description}}
                {{/if}}
            </article>
            {{product.detail_messages}}
            <dl class="productView-info">
                {{#if product.condition}}
                    <dt class="productView-info-name">{{lang 'products.condition'}}</dt>
                    <dd class="productView-info-value">{{product.condition}}</dd>
                {{/if}}
                {{#all product.weight theme_settings.show_product_weight}}
                    <dt class="productView-info-name">{{lang 'products.weight'}}</dt>
                    <dd class="productView-info-value" data-product-weight>{{product.weight}}</dd>
                {{/all}}
                {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
                    <dt class="productView-info-name">{{lang 'products.width'}}</dt>
                    <dd class="productView-info-value" data-product-width>
                        {{product.width}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                    <dt class="productView-info-name">{{lang 'products.height'}}</dt>
                    <dd class="productView-info-value" data-product-height>
                        {{product.height}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                    <dt class="productView-info-name">{{lang 'products.depth'}}</dt>
                    <dd class="productView-info-value" data-product-depth>
                        {{product.depth}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                {{/all}}
                {{#if product.min_purchase_quantity}}
                    <dt class="productView-info-name">{{lang 'products.min_purchase_quantity'}}</dt>
                    <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.min_purchase_quantity}}</dd>
                {{/if}}
                {{#if product.max_purchase_quantity}}
                    <dt class="productView-info-name">{{lang 'products.max_purchase_quantity'}}</dt>
                    <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.max_purchase_quantity}}</dd>
                {{/if}}
                {{#if product.gift_wrapping_available}}
                    <dt class="productView-info-name">{{lang 'products.gift_wrapping'}}</dt>
                    <dd class="productView-info-value">{{lang 'products.gift_wrapping_available'}}</dd>
                {{/if}}
                {{#if product.shipping}}
                    {{#if product.shipping.calculated}}
                        <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.shipping_calculated'}}</dd>
                    {{else}}
                        {{#if product.shipping.price.value '===' 0}}
                            <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                            <dd class="productView-info-value">{{lang 'products.shipping_free'}}</dd>
                        {{else}}
                            <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                            <dd class="productView-info-value">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</dd>
                        {{/if}}
                    {{/if}}
                {{/if}}

                {{#if settings.bulk_discount_enabled}}
                    <div class="productView-info-bulkPricing">
                        {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
                    </div>
                {{/if}}
            </dl>
        </div>
        {{#if customer }}
        {{#if product.can_purchase}}
        <div class="shipping-message">
            <svg width="21" height="16" viewBox="0 0 21 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M20 11.5002H19.5V7.87524C19.5 7.28149 19.25 6.71899 18.8125 6.28149L16.2188 3.68774C15.7812 3.25024 15.2188 3.00024 14.625 3.00024H13.5V1.75024C13.5 0.812744 12.6875 0.000244141 11.75 0.000244141H2.25C1.28125 0.000244141 0.5 0.812744 0.5 1.75024V11.2502C0.5 12.219 1.28125 13.0002 2.25 13.0002H2.5C2.5 14.6565 3.84375 16.0002 5.5 16.0002C7.15625 16.0002 8.5 14.6565 8.5 13.0002H12.5C12.5 14.6565 13.8438 16.0002 15.5 16.0002C17.1562 16.0002 18.5 14.6565 18.5 13.0002H20C20.25 13.0002 20.5 12.7815 20.5 12.5002V12.0002C20.5 11.7502 20.25 11.5002 20 11.5002ZM5.5 14.5002C4.65625 14.5002 4 13.844 4 13.0002C4 12.1877 4.65625 11.5002 5.5 11.5002C6.3125 11.5002 7 12.1877 7 13.0002C7 13.844 6.3125 14.5002 5.5 14.5002ZM12 11.5002H8.0625C7.5625 10.6252 6.59375 10.0002 5.5 10.0002C4.375 10.0002 3.40625 10.6252 2.90625 11.5002H2.25C2.09375 11.5002 2 11.4065 2 11.2502V1.75024C2 1.62524 2.09375 1.50024 2.25 1.50024H11.75C11.875 1.50024 12 1.62524 12 1.75024V11.5002ZM13.5 4.50024H14.625C14.8125 4.50024 15 4.59399 15.1562 4.71899L17.4375 7.00024H13.5V4.50024ZM15.5 14.5002C14.6562 14.5002 14 13.844 14 13.0002C14 12.1877 14.6562 11.5002 15.5 11.5002C16.3125 11.5002 17 12.1877 17 13.0002C17 13.844 16.3125 14.5002 15.5 14.5002ZM18 11.3752C17.4375 10.5627 16.5312 10.0002 15.5 10.0002C14.7188 10.0002 14.0312 10.3127 13.5 10.7815V8.50024H18V11.3752Z" fill="#FFB500"/>
            </svg>
            <p>{{product.availability}}</p> 
        {{else}}
        <div class="shipping-message">
            <svg width="21" height="16" viewBox="0 0 21 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M20 11.5002H19.5V7.87524C19.5 7.28149 19.25 6.71899 18.8125 6.28149L16.2188 3.68774C15.7812 3.25024 15.2188 3.00024 14.625 3.00024H13.5V1.75024C13.5 0.812744 12.6875 0.000244141 11.75 0.000244141H2.25C1.28125 0.000244141 0.5 0.812744 0.5 1.75024V11.2502C0.5 12.219 1.28125 13.0002 2.25 13.0002H2.5C2.5 14.6565 3.84375 16.0002 5.5 16.0002C7.15625 16.0002 8.5 14.6565 8.5 13.0002H12.5C12.5 14.6565 13.8438 16.0002 15.5 16.0002C17.1562 16.0002 18.5 14.6565 18.5 13.0002H20C20.25 13.0002 20.5 12.7815 20.5 12.5002V12.0002C20.5 11.7502 20.25 11.5002 20 11.5002ZM5.5 14.5002C4.65625 14.5002 4 13.844 4 13.0002C4 12.1877 4.65625 11.5002 5.5 11.5002C6.3125 11.5002 7 12.1877 7 13.0002C7 13.844 6.3125 14.5002 5.5 14.5002ZM12 11.5002H8.0625C7.5625 10.6252 6.59375 10.0002 5.5 10.0002C4.375 10.0002 3.40625 10.6252 2.90625 11.5002H2.25C2.09375 11.5002 2 11.4065 2 11.2502V1.75024C2 1.62524 2.09375 1.50024 2.25 1.50024H11.75C11.875 1.50024 12 1.62524 12 1.75024V11.5002ZM13.5 4.50024H14.625C14.8125 4.50024 15 4.59399 15.1562 4.71899L17.4375 7.00024H13.5V4.50024ZM15.5 14.5002C14.6562 14.5002 14 13.844 14 13.0002C14 12.1877 14.6562 11.5002 15.5 11.5002C16.3125 11.5002 17 12.1877 17 13.0002C17 13.844 16.3125 14.5002 15.5 14.5002ZM18 11.3752C17.4375 10.5627 16.5312 10.0002 15.5 10.0002C14.7188 10.0002 14.0312 10.3127 13.5 10.7815V8.50024H18V11.3752Z" fill="#FFB500"/>
            </svg>
            <p> {{product.availability}} </p>
            {{/if}}
        </div>
        <div class="special-note">
            <p>Please note: subscriptions are not allowed on P.O. orders.</p>
        </div>
        {{/if}}
        <div class="product-options">
            <div class="productView-options">
                <form class="form" method="post" action="{{product.cart_url}}" enctype="multipart/form-data"
                      data-cart-item-add>
                    <input type="hidden" name="action" value="add">
                    <input type="hidden" name="product_id" value="{{product.id}}"/>
                    <div data-product-option-change style="display:none;">
                        {{inject 'showSwatchNames' theme_settings.show_product_swatch_names}}
                        {{#each product.options}}
                            {{{dynamicComponent 'components/products/options'}}}
                        {{/each}}
                    </div>
                    <div class="product-container-add{{#if customer }} customer-log-in{{else}} customer-log-out{{/if}}">
                        {{> components/products/add-to-cart}}
                        {{#if product.out_of_stock}}
                            {{#if product.out_of_stock_message}}
                                {{> components/common/alert/alert-error product.out_of_stock_message}}
                            {{else}}
                                {{> components/common/alert/alert-error (lang 'products.sold_out')}}
                            {{/if}}
                        {{/if}}
                    </div>
                </form>
                {{#if settings.show_wishlist}}
                    {{> components/common/wishlist-dropdown}}
                {{/if}}
            </div>
        </div>
        {{#if product.warranty}}
            <div class="product-addition-details">
                <h3>{{lang 'products.warranty'}}</h3>
                {{{product.warranty}}}
            </div>
        {{/if}}
        </div>
    </section>

</div>

<div id="previewModal" class="modal modal--large" data-reveal>
    {{> components/common/modal/modal-close-btn }}
    <div class="modal-content"></div>
    <div class="loadingOverlay"></div>
</div>

components/products/reviews:
{{#if reviews.total}}
<section class="toggle productReviews" id="product-reviews" data-product-reviews>
    <h4 class="toggle-title">
        {{lang 'products.reviews.header' total=reviews.total}}
        {{#if theme_settings.show_product_reviews}}
            <a class="toggleLink is-open" data-collapsible href="#productReviews-content">
                <span class="toggleLink-text toggleLink-text--on">
                    {{lang 'products.reviews.hide'}}
                </span>
                <span class="toggleLink-text toggleLink-text--off">
                    {{lang 'products.reviews.show'}}
                </span>
            </a>
        {{/if}}
    </h4>
    <div class="toggle-content is-open" id="productReviews-content" aria-hidden="false">
        <ul class="productReviews-list" id="productReviews-list">
            {{#each reviews.list}}
            <li class="productReview">
                <article itemprop="review" itemscope itemtype="https://schema.org/Review">
                    <header>
                        <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="https://schema.org/Rating">
                            {{> components/products/ratings rating=rating}}
                            <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                        </span>
                        <h5 itemprop="name" class="productReview-title">{{{ sanitize title }}}</h5>
                        {{#if name}}
                            <meta itemprop="author" content="{{name}}">
                            <p class="productReview-author">
                                {{{lang 'products.reviews.post_on_by' date=date name=(sanitize name) }}}
                            </p>
                        {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p itemprop="reviewBody" class="productReview-body">{{{ sanitize text }}}</p>
                </article>
            </li>
            {{/each}}
        </ul>
        {{> components/common/paginator pagination.reviews reload=true}}
    </div>
</section>
{{/if}}
